class SignalProcessor
    def self.is_signal_marker?(segment, length)
        return segment.split('').uniq.length == length && segment.split('').length == length
    end

    def self.find_start_of_message(data_stream)
        n = 13
        while n < data_stream.length
            if(is_signal_marker?(data_stream[n-13..n], 14))
                return n+1
            end
            n += 1
        end
        -1        
    end 
    def self.find_signal_marker(data_stream)
        n = 3
        while n < data_stream.length
            if(is_signal_marker?(data_stream[n-3..n], 4))
                return n+1
            end
            n += 1
        end
        -1
    end
end


